import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Link, Redirect } from 'react-router-dom';
import './App.css';

import Home from './components/Home'
import Account from './components/Account'
import Dashboard from './components/Dashboard'

function App() {
  const [ isAuth, setIsAuth ] = useState(true);
  useEffect(() => {
    //
  }, [])

  const logIn = () => {
    //
  }

  const logOut = (e) => {
    e.preventDefault();
    setIsAuth(prevState => !prevState)
  }

  return (
    <Router>
      {/* if logged in - go to dashboard - else - homepage */}
      { isAuth ? <Redirect to="/dashboard" /> : <Redirect to="/" /> }
      <div className="App">
        <div className="header">
          <div className="logo">
            <Link to="/">SiteName</Link>
          </div>
          <div className="nav">
            <div className="nav-wrap">
              <Link to="/">Home</Link>
              { isAuth ? <Link to="/dashboard">Dashboard</Link> : <Link to="/account">Dashboard</Link> }
              { isAuth ? <a href="#" onClick={ logOut }>Logout</a> : <Link to="/account">Login</Link> }
            </div>
          </div>
        </div>

        <Switch>
          <Route exact path="/">
            <Home />
          </Route>

          { isAuth ? (
            <Route path="/dashboard">
              <Dashboard
                isAuth={isAuth}
              />
            </Route>
          ) : (
            <Route path="/account">
              <Account
                isAuth={isAuth}
              />
            </Route>
          )}
          <Route path="/account">
            <Account
              isAuth={isAuth}
            />
          </Route>
        </Switch>

      </div>
    </Router>
  );
}

export default App;
