import React, { useState, useEffect } from 'react'

export default function Account() {
  const [ form, setForm ] = useState('login')

  const changeForm = (e, type) => {
    e.preventDefault();
    if(type === 'login') {
      setForm('login');
    } else if (type === 'signup') {
      setForm('signup');
    } else {
      setForm('login');
    }
  }

  return (
    <div className="page-wrap">
      <h4>Login or Create Account</h4>
      {form === 'login' ? (
        <div className="account login">
          <h4>Login</h4>
          <div onClick={ (e) => changeForm(e, signup) }>Don't have account? Create New Account.</div>
        </div>
      ) : null }
      {form === 'signup' ? (
        <div className="account signup">
          <h4>Create Account</h4>
          <div onClick={ (e) => changeForm(e, login) }>Already have account? Login.</div>
        </div>
      ) : null }
    </div>
  )
}
